lang:groovy.lang:inline-script:
import com.dewen.service.GroovyService

class HelloServiceImpl implements GroovyService {

    String name;

    @Override
    String sayHello() {
        return "Hello $name. Welcome to static script in Groovy.";
    }

    @Override
    String sayHello(String name) {
        return "Hello $name. Welcome to static script in Groovy.";
    }
}
